mapper.select:
SELECT eva.id, eva.score, eva.content, eva.create_date AS createDate, oi.code, oi.price, oi.amount, ui.name AS userName, ui.avatar, si.scenic_name AS scenicName FROM evaluation eva LEFT JOIN scenic_order oi ON (oi.id = eva.order_id) LEFT JOIN user_info ui ON ( ui.id = oi.user_id ) LEFT JOIN scenic_info si ON ( si.id = oi.scenic_id ) WHERE 1 = 1 AND eva.type = 1
AND eva.scenic_id = #{scenicId}
ORDER BY eva.create_date DESC
